    methods: {
      showMoreFacilities() {
        if (!this.show_more) {
          this.show_more = true;
          this.shown_facilityItems = [];
          let round = 0;
          const interval = setInterval(() => {
            while ( round < this.facility_items.length && this.$props.facilities[this.facility_items[round]].length === 0) {
              round++;
            }
            if (round < this.facility_items.length) {
              this.shown_facilityItems.push(this.facility_items[round]);
              round++;
            } else {
              clearInterval(interval);
            }
          }, 40);
        } else {
          this.show_more = false;
          const interval = setInterval(() => {
            if (this.shown_facilityItems.length > 3)
              this.shown_facilityItems.pop();
            if (this.shown_facilityItems.length <= 3)
              clearInterval(interval);
          }, 40);


        }
      }
    },
    props: {
      facilities: {
        type: Object
      }
    },
    watch: {
      facilities: function () {
        let counter = 0;
        let i = 0;
        this.shown_facilityItems = [];
        while (counter < 3 && i < this.facility_items.length) {
          if (this.$props.facilities[this.facility_items[i]].length > 0) {
            this.shown_facilityItems.push(this.facility_items[i]);
            counter++;
          }
          i++;
        }
        console.log(this.shown_facilityItems)
      }
    }
  }
